mapper.select:
SELECT

FROM t_bastax_taxcode


        AND fid = #{condition.id}


        AND fnumber = #{condition.number}


        AND fstatus = #{condition.status}


        AND fcreatorid = #{condition.creatorid}


        AND fmodifierid = #{condition.modifierid}


        AND fenable = #{condition.enable}


        AND fcreatetime = #{condition.createtime}


        AND fmodifytime = #{condition.modifytime}


        AND fmasterid = #{condition.masterid}


        AND fcreateorgid = #{condition.createorgid}


        AND forgid = #{condition.orgid}


        AND fuseorg = #{condition.useorg}


        AND fctrlstrategy = #{condition.ctrlstrategy}


        AND ftaxcodetype = #{condition.taxcodetype}


        AND fcountry = #{condition.country}


        AND ftaxcodeproperty = #{condition.taxcodeproperty}


        AND ftaxtype = #{condition.taxtype}


        AND fcontainstax = #{condition.containstax}


        AND fstartdate = #{condition.startdate}


        AND fenddate = #{condition.enddate}


        AND foffsetlogo = #{condition.offsetlogo}


        AND fsourcedataid = #{condition.sourcedataid}


        AND fbitindex = #{condition.bitindex}


        AND fsourcebitindex = #{condition.sourcebitindex}


        AND ftaxationsys = #{condition.taxationsys}


        AND ftaxareagroup = #{condition.taxareagroup}


        AND foffsettax = #{condition.offsettax}


        AND fimpactcost = #{condition.impactcost}


        AND fdeductible = #{condition.deductible}


        AND fsrccreateorgid = #{condition.srccreateorgid}


ORDER BY fid DESC

SELECT COUNT(*)
FROM t_bastax_taxcode


        AND fid = #{condition.id}


