mapper.select:
SELECT

FROM t_bd_country


        AND fid = #{condition.id}


        AND fnumber = #{condition.number}


        AND fenable = #{condition.enable}


        AND fstatus = #{condition.status}


        AND fcreateorgid = #{condition.createorgid}


        AND forgid = #{condition.orgid}


        AND fcreatorid = #{condition.creatorid}


        AND fcreatetime = #{condition.createtime}


        AND fmodifyorgid = #{condition.modifyorgid}


        AND fmodifierid = #{condition.modifierid}


        AND fmodifytime = #{condition.modifytime}


        AND flogo = #{condition.logo}


        AND fdisablerid = #{condition.disablerid}


        AND fdisabledate = #{condition.disabledate}


        AND fmasterid = #{condition.masterid}


        AND fsimplespell = #{condition.simplespell}


        AND ftwocountrycode = #{condition.twocountrycode}


        AND fthreecountrycode = #{condition.threecountrycode}


        AND fformatplanid = #{condition.formatplanid}


        AND fareacode = #{condition.areacode}


        AND fname = #{condition.name}


        AND ffullname = #{condition.fullname}


        AND fdescription = #{condition.description}


        AND fissystem = #{condition.issystem}


        AND fnumericcode = #{condition.numericcode}


        AND ftimezone = #{condition.timezone}


        AND fregionalformat = #{condition.regionalformat}


ORDER BY fid DESC

SELECT COUNT(*)
FROM t_bd_country


        AND fid = #{condition.id}


        AND fnumber = #{condition.number}


        AND fenable = #{condition.enable}


        AND fstatus = #{condition.status}


        AND fcreateorgid = #{condition.createorgid}


        AND forgid = #{condition.orgid}


