        AND fmodifytime = #{condition.modifytime}


        AND flogo = #{condition.logo}


        AND fdisablerid = #{condition.disablerid}


        AND fdisabledate = #{condition.disabledate}


        AND fmasterid = #{condition.masterid}


        AND fsimplespell = #{condition.simplespell}


        AND ftwocountrycode = #{condition.twocountrycode}


        AND fthreecountrycode = #{condition.threecountrycode}


        AND fformatplanid = #{condition.formatplanid}


        AND fareacode = #{condition.areacode}


        AND fname = #{condition.name}


        AND ffullname = #{condition.fullname}


        AND fdescription = #{condition.description}


        AND fissystem = #{condition.issystem}


