mapper.update:
UPDATE t_invoice_line
finvoice_request_id = #{invoiceRequestId,jdbcType=BIGINT}, fseq = #{seq,jdbcType=INTEGER}, finvoice_line_amount = #{invoiceLineAmount,jdbcType=DECIMAL}, fstatus = #{status,jdbcType=TINYINT}, fhas_multiline = #{hasMultiline,jdbcType=TINYINT}, finvoice_id = #{invoiceId,jdbcType=BIGINT}, famount = #{amount,jdbcType=DECIMAL}, ftype = #{type,jdbcType=TINYINT}, fitem_name = #{itemName,jdbcType=VARCHAR}, funit = #{unit,jdbcType=VARCHAR}, fquantity = #{quantity,jdbcType=DECIMAL}, fprice = #{price,jdbcType=DECIMAL}, fcategory_code = #{categoryCode,jdbcType=VARCHAR}, ftax_rate = #{taxRate,jdbcType=DECIMAL}, fext_field = #{extField,jdbcType=LONGVARCHAR}, fupdate_time = #{updateTime,jdbcType=TIMESTAMP},
WHERE fid = #{id,jdbcType=BIGINT}

mapper.select:
SELECT 
FROM t_invoice_line


        AND finvoice_request_id = #{invoiceRequestId,jdbcType=BIGINT}


        AND finvoice_id = #{invoiceId,jdbcType=BIGINT}


        AND fstatus = #{status,jdbcType=TINYINT}


        AND ftype = #{type,jdbcType=TINYINT}


        AND fitem_name LIKE CONCAT('%', #{itemName,jdbcType=VARCHAR}, '%')


        AND fcategory_code = #{categoryCode,jdbcType=VARCHAR}


ORDER BY fcreate_time DESC
