mapper.select:
SELECT

FROM t_sec_user_u


        AND fid = #{condition.id}


        AND ftype = #{condition.type}


        AND fislocked = #{condition.islocked}


        AND fisforbidden = #{condition.isforbidden}


        AND fauthorstatus = #{condition.authorstatus}


        AND fpassword = #{condition.password}


        AND fisregisted = #{condition.isregisted}


        AND fisactived = #{condition.isactived}


        AND fusername = #{condition.username}


        AND fpswhisstr = #{condition.pswhisstr}


        AND fpswstrategyid = #{condition.pswstrategyid}


        AND fpsweffectivedate = #{condition.psweffectivedate}


        AND ferrcount = #{condition.errcount}


        AND flockedtime = #{condition.lockedtime}


        AND fexternaluuid = #{condition.externaluuid}


        AND flastloginip = #{condition.lastloginip}


        AND flastlogintime = #{condition.lastlogintime}


        AND fuserdisablerid = #{condition.userdisablerid}


        AND fuserdisabletime = #{condition.userdisabletime}


        AND fuseenddate = #{condition.useenddate}


ORDER BY fid DESC

SELECT COUNT(*)
FROM t_sec_user_u


        AND fid = #{condition.id}


        AND ftype = #{condition.type}


        AND fislocked = #{condition.islocked}


        AND fisforbidden = #{condition.isforbidden}


        AND fauthorstatus = #{condition.authorstatus}


        AND fpassword = #{condition.password}


        AND fisregisted = #{condition.isregisted}


        AND fisactived = #{condition.isactived}


        AND fusername = #{condition.username}


        AND fpswhisstr = #{condition.pswhisstr}


        AND fpswstrategyid = #{condition.pswstrategyid}


        AND fpsweffectivedate = #{condition.psweffectivedate}


        AND ferrcount = #{condition.errcount}


        AND flockedtime = #{condition.lockedtime}


        AND fexternaluuid = #{condition.externaluuid}


        AND flastloginip = #{condition.lastloginip}


        AND flastlogintime = #{condition.lastlogintime}


        AND fuserdisablerid = #{condition.userdisablerid}


        AND fuserdisabletime = #{condition.userdisabletime}


        AND fuseenddate = #{condition.useenddate}

mapper.delete:
